def compare(user_score, computer_score):
    if user_score == computer_score:
        return 'draw'
    elif computer_score == 0:
        return 'lose'
    elif user_score == 0:
        return 'win'
    elif user_score > computer_score:
        return 'win'
    else:
        return 'lose'

