solution_code:
class BankAccount:
    def __init__(self):
        self.balance = 0

    def deposit(self, amount):
        self.balance += amount

    def withdraw(self, amount):
        if amount > self.balance:
            return False
        self.balance -= amount
        return True

def test_account(deposits, withdrawals):
    acc = BankAccount()
    for d in deposits:
        acc.deposit(d)
    for w in withdrawals:
        acc.withdraw(w)
    return acc.balance
