class Question:
    def __init__(self, text, answer):
        self.text = text
        self.answer = answer

def quiz_score(questions, user_answers):
    correct = 0
    for i, q in enumerate(questions):
        if user_answers[i].lower() == q.answer.lower():
            correct += 1
    return f'{correct}/{len(questions)}'

