solution_code:
class Question:
    def __init__(self, text, answer):
        self.text = text
        self.answer = answer

def build_question_bank(question_data):
    question_bank = []
    for item in question_data:
        q = Question(item['text'], item['answer'])
        question_bank.append(q)
    return question_bank

def test_build(data):
    questions = build_question_bank(data)
    return len(questions)
