import random

def random_rgb():
    r = random.randint(0, 255)
    g = random.randint(0, 255)
    b = random.randint(0, 255)
    return (r, g, b)

def test_rgb():
    rgb = random_rgb()
    return len(rgb) == 3 and all(0 <= v <= 255 for v in rgb)

