def count_lines():
    with open('data.txt', 'r') as f:
        return len(f.readlines())

import random
import string

def generate_password(n):
    return ''.join(random.choices(string.ascii_lowercase, k=n))

def parse_entry(entry):
    parts = entry.split(' | ')
    return (parts[0], parts[1])

def read_first_line():
    with open('data.txt', 'r') as f:
        return f.readline().strip()
