def absolute_value(num):
    if num < 0:
        return -num
    else:
        return num

def ticket_price(age):
    if age < 12:
        return 5
    elif age <= 18:
        return 7
    else:
        return 12

def grade(score):
    if score >= 90:
        return 'A'
    elif score >= 80:
        return 'B'
    elif score >= 70:
        return 'C'
    elif score >= 60:
        return 'D'
    else:
        return 'F'
