def get_popup_title(driver):
    original = driver.current_window_handle
    for handle in driver.window_handles:
        if handle != original:
            driver.switch_to.window(handle)
            return driver.title
    return None

def dismiss_modal(driver):
    from selenium.webdriver.common.by import By
    try:
        btn = driver.find_element(By.XPATH, '//button[contains(text(), "No Thanks") or contains(text(), "Close")]')
        btn.click()
        return True
    except:
        return False

def safe_dismiss(driver, button_xpath):
    from selenium.webdriver.common.by import By
    from selenium.common.exceptions import NoSuchElementException
    try:
        button = driver.find_element(By.XPATH, button_xpath)
        button.click()
        return True
    except NoSuchElementException:
        return False

def swipe_loop(driver, like_button_locator, count=10):
    import time
    from selenium.webdriver.common.by import By
    for _ in range(count):
        try:
            button = driver.find_element(like_button_locator[0], like_button_locator[1])
            button.click()
            time.sleep(1)
        except:
            pass

def enter_iframe(driver, frame_name):
    try:
        driver.switch_to.frame(frame_name)
        return True
    except:
        return False
