from flask import Flask
app = Flask(__name__)

def create_greeting_route():
    @app.route('/greet/<name>')
    def greet(name):
        return f'Hello, {name}!'
    return greet

from flask import Flask
app = Flask(__name__)

def create_square_route():
    @app.route('/square/<int:num>')
    def square(num):
        return str(num ** 2)
    return square

def universal_decorator(func):
    def wrapper(*args, **kwargs):
        print(f'Calling {func.__name__}')
        return func(*args, **kwargs)
    return wrapper

def check_guess(guess, secret):
    if guess < secret:
        return 'Too low!'
    elif guess > secret:
        return 'Too high!'
    else:
        return 'Correct!'

