import requests

def fetch_api(url):
    response = requests.get(url)
    if response.status_code == 200:
        return response.json()
    return None

import requests

def fetch_with_timeout(url, timeout_seconds=5):
    try:
        response = requests.get(url, timeout=timeout_seconds)
        return response.json()
    except requests.exceptions.Timeout:
        return None

