from pathlib import Path

def get_files_by_extension(folder, extension):
    return list(Path(folder).glob(f'*{extension}'))

from pathlib import Path
import shutil

def organize_by_extension(folder):
    folder = Path(folder)
    for file in folder.iterdir():
        if file.is_file():
            ext = file.suffix.lower() or 'no_extension'
            dest = folder / ext[1:]  # Remove dot
            dest.mkdir(exist_ok=True)
            shutil.move(str(file), str(dest / file.name))

from pathlib import Path

def add_prefix(folder, prefix):
    for file in Path(folder).iterdir():
        if file.is_file():
            new_name = file.parent / f'{prefix}{file.name}'
            file.rename(new_name)
